import { Button, Container, Typography } from '@mui/material';
import React from 'react';
import ModalForm from './ModalForm';
import { useState } from 'react';
import { useContext } from 'react';
import NoteContext from '../context/NoteContext';
import NoteCard from './'

function Home() {
  let context= useContext(NoteContext)
  const {notes,setNotes}= context
    const [modal,setModal]=useState(false)
    const handleOpen=()=>{
        setModal(true)
    }
    const handleClose=()=>{
        setModal(false)
    }
  return (
    <>
      <Container>
        <Typography variant="h4" sx={{marginBlock:3}}>To Create Note Press the Button</Typography>
      <Button size='large' variant='contained' onClick={handleOpen}>Create A Note</Button>
      <Typography variant="h4" sx={{marginBlock:3}}>Your Notes:</Typography>
        {notes.map((elem, index) => (
      <Typography key={index} variant="body1">
        {elem.title}
      </Typography>
    ))}
      </Container>

        <ModalForm
            open={modal}
            handleClose={handleClose}
        />
    </>
  );
}

export default Home;
