import { Typography, Box, Container } from "@mui/material";
import React, { useContext, useEffect, useState } from "react";
import UserContext from "../context/UserContext";

const NAVBAR_HEIGHT = 64;

const Welcome = () => {
    let context = useContext(UserContext);
    const { fetchUserData, user } = context;
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const checkAuth = async () => {
            setLoading(true);
            await fetchUserData();
            setLoading(false);
        };
        checkAuth();
    }, []); // Only run once on mount

    const renderContent = () => {
        if (loading) {
            return (
                <Typography variant="h3" fontWeight="bold" sx={{px: 30}}>
                    Loading...
                </Typography>
            );
        }

        // Check if user exists and has required data
        if (!user || !user.name) {
            return (
                <Typography variant="h3" fontWeight="bold" sx={{px: 30}}>
                    Welcome to iNoteBook, Please Login or Signup to Continue
                </Typography>
            );
        }

        // User is authenticated and has data
        return (
            <Typography variant="h3" fontWeight="bold" sx={{px: 30}}>
                Welcome {user.name}, Feel Free to Make Notes!
            </Typography>
        );
    };

    return (
        <Container>
            <Box
                sx={{
                    height: `calc(100vh - ${NAVBAR_HEIGHT}px)`,
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    textAlign: "center",
                    padding: 2,
                    boxSizing: "border-box"
                }}
            >
                {renderContent()}
            </Box>
        </Container>
    );
};

export default Welcome;
