import React, { useState } from "react"; 
import NoteContext from "./NoteContext";

export default function NoteState(props) {
  const host="http://localhost:5000/"
  createNote="/createNote"
  updateNote="/updatenote/:id put"
  delteNote="/deleteNote/:id put"
   const [notes, setNotes] = useState([]);
  const hardcodeNotes = [ ];
const getNotes = async () => {
  const response = await fetch(`${host}api/notes/fetchallnotes`, {
    method: 'GET',
    headers: {
      'Accept': '*/*',
      'User-Agent': 'Thunder Client (https://www.thunderclient.com)',
      'auth-token': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjg5MWZkNGQyNGQ1ODJhZjE1YjliN2JlIn0sImlhdCI6MTc1NDQ2MzgwMn0.gKhLAuMPCQNf0DjGdAzTVgHa85VmItPk408CXuOdMe8',
      'Content-Type': 'application/json'
    }
  });
  const allNotes = await response.json(); 
  console.log(allNotes);
  setNotes(allNotes)
};

async function addNote(title1, description1, tag1) {
  const response = await fetch(`${host}api/notes/createNote`, {
    method: 'POST', // Use POST for create
    headers: {
      'Content-Type': 'application/json',
      'auth-token': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjg5MWZkNGQyNGQ1ODJhZjE1YjliN2JlIn0sImlhdCI6MTc1NDQ2MzgwMn0.gKhLAuMPCQNf0DjGdAzTVgHa85VmItPk408CXuOdMe8'
    },
    body: JSON.stringify({ title: title1, description: description1, tag: tag1 })
  });

  const json = await response.json(); // actual created note from backend

  // Update UI
  setNotes(notes.concat(json));
}
async function deleteNote(id) {
  const response = await fetch(`${host}api/notes/deleteNote/6891fd4d24d582af15b9b7be`, {
    method: 'DELETE',
    headers: {
      'Content-Type': 'application/json',
      'auth-token': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjg5MWZkNGQyNGQ1ODJhZjE1YjliN2JlIn0sImlhdCI6MTc1NDQ2MzgwMn0.gKhLAuMPCQNf0DjGdAzTVgHa85VmItPk408CXuOdMe8'
    }
  });

  console.log("The ID is: " + id);

  // Remove the note from state
  setNotes(notes.filter((note) => note._id !== id));
}

async function editNote(id, title, description, tag) {
  const response = await fetch(`${host}api/notes/updatenote/6891fd4d24d582af15b9b7be`, {
    method: 'PUT',
    headers: {
      'Content-Type': 'application/json',
      'auth-token': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjg5MWZkNGQyNGQ1ODJhZjE1YjliN2JlIn0sImlhdCI6MTc1NDQ2MzgwMn0.gKhLAuMPCQNf0DjGdAzTVgHa85VmItPk408CXuOdMe8'
    },
    body: JSON.stringify({ title, description, tag })
  });

  const updatedNote = await response.json();

  const newNotes = notes.map(note => {
    if (note._id === id) {
      return updatedNote; 
    }
    return note;
  });

  setNotes(newNotes);
}

  return (
    <NoteContext.Provider value={{notes,addNote,deleteNote,getNotes,editNote}}>
      {props.children}
    </NoteContext.Provider>
  );
}
