import { Typography, Box, Container } from "@mui/material";
import React, { useContext } from "react";
import UserContext from "../context/UserContext";
const NAVBAR_HEIGHT = 64; 

const Welcome = () => {
    let context=useContext(UserContext);
    const {fetchUserData,user} = context
    const UserCheck= async ()=>{
        let result=await fetchUserData();
        if(result.success)
        {
            return true;
        }else{
            return false;
        }
    }
  return (
    <Container>
        <Box
        sx={{
            height: `calc(100vh - ${NAVBAR_HEIGHT}px)`, 
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            textAlign: "center",
            padding: 2,
            boxSizing:"border-box"
        }}
        >
           {(!UserCheck)?
        <Typography variant="h3" fontWeight="bold" sx={{px:30}}>
            Welcome to iNoteBook, Please Login or Signup to Continue
        </Typography>: 
        <Typography variant="h3" fontWeight="bold" sx={{px:30}}>
            Welcome {user.name},Feel Free to Make Notes
        </Typography>
        }
        </Box>
    </Container>
  );
};

export default Welcome;
