import { Button, Container, Grid, Typography } from '@mui/material';
import React from 'react';
import ModalForm from './ModalForm';
import { useState } from 'react';
import { useContext } from 'react';
import NoteContext from '../context/NoteContext';
import NoteCard from './NoteCard'

function Home() {
  let context= useContext(NoteContext)
  const {notes,setNotes}= context
  // const {title,description,tag}=notes;
    const [modal,setModal]=useState(false)
    const handleOpen=()=>{
        setModal(true)
    }
    const handleClose=()=>{
        setModal(false)
    }
  return (
    <>
      <Container>
        <Typography variant="h4" sx={{marginBlock:3}}>To Create Note Press the Button</Typography>
      <Button size='large' variant='contained' onClick={handleOpen}>Create A Note</Button>
      <Typography variant="h4" sx={{marginBlock:3}}>Your Notes:</Typography>
 <Grid container spacing={2}>
  {notes.map((note) => (
    <Grid item xs={12} sm={6} md={4} key={note._id || index}>
      <NoteCard 
        title={note.title} 
        description={note.description} 
        tag={note.tag}
      />
    </Grid>
  ))}
</Grid>
      </Container>

        <ModalForm
            open={modal}
            handleClose={handleClose}
        />
    </>
  );
}

export default Home;
