import React, { useState } from 'react'
import { Paper, Box, Typography, TextField, Button, Container } from "@mui/material";

const LoginPage = () => {
    const[credentials,setCredentials]=useState({email:"",password:""})
      const handleChange = (e) => {
        setCredentials({ ...credentials, [e.target.name]: e.target.value });
    };
  const loginCheck=  async () => {
        const response = await fetch(`http://localhost:5000/api/auth/login`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({email: credentials.email, password: credentials.password})
        });
        let json = await response.json();
        if(json.success){
            localStorage.setItem(json.authToken)
            
        }
    }
    const handleSubmit = (e) => {
        e.preventDefault()
        loginCheck();
    }
  return (
    <Container>
    <Box
      display="flex"
      justifyContent="center"
      alignItems="center"
      flexGrow={1} 
      py={10}
    >
      <Paper
        elevation={3}
        sx={{
          p: 4,
          width: 350,
          display: "flex",
          flexDirection: "column",
          gap: 2,
        }}
      >
        <Typography variant="h5" textAlign="center">
          Login
        </Typography>
      <TextField label="Email" name='email' value={credentials.email} onChange={handleChange} variant="outlined" fullWidth />
      <TextField label="Password" name='password' value={credentials.password} type="password" onChange={handleChange} variant="outlined" fullWidth />
        <Button variant="contained"  onClick={handleSubmit} fullWidth>
          Login
        </Button>
      </Paper>
    </Box>
    </Container>
  );
};


export default LoginPage;
