import React, { useState } from 'react';
import { Modal, Box, Typography, TextField, Button, Stack } from '@mui/material';

const modalStyle = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  border: '2px solid #000',
  boxShadow: 24,
  p: 4,
};

// This component receives props from the parent
const ModalForm = ({ open, handleClose, onSaveNote }) => {
  const [noteTitle, setNoteTitle] = useState('');
  const [noteContent, setNoteContent] = useState('');

  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (noteTitle && noteContent) {
      onSaveNote({ title: noteTitle, content: noteContent });
      // Reset the form fields after submission
      setNoteTitle('');
      setNoteContent('');
    }
    // Close the modal whether the form was valid or not (optional)
    handleClose();
  };

  return (
    <Modal
      open={open}
      onClose={handleClose}
      aria-labelledby="create-note-modal-title"
      aria-describedby="create-note-modal-description"
    >
      <Box sx={modalStyle}>
        <Typography id="create-note-modal-title" variant="h6" component="h2">
          Create a New Note
        </Typography>

        <Box component="form" onSubmit={handleFormSubmit} sx={{ mt: 2 }}>
          <Stack spacing={2}>
            <TextField
              required
              fullWidth
              label="Note Title"
              value={noteTitle}
              onChange={(e) => setNoteTitle(e.target.value)}
            />
            <TextField
              required
              fullWidth
              multiline
              rows={4}
              label="Note Content"
              value={noteContent}
              onChange={(e) => setNoteContent(e.target.value)}
            />
          </Stack>
          <Button type="submit" variant="contained" sx={{ mt: 2 }}>
            Save Note
          </Button>
        </Box>
      </Box>
    </Modal>
  );
};

export default ModalForm;
