import { Typography, Box, Container, CircularProgress } from "@mui/material";
import React, { useContext, useEffect, useState } from "react";
import UserContext from "../context/UserContext";

const NAVBAR_HEIGHT = 64;

const Welcome = () => {
  const { fetchUserData, user } = useContext(UserContext);
  const [authStatus, setAuthStatus] = useState('checking'); // 'checking' | 'authenticated' | 'unauthenticated'

  useEffect(() => {
    let isMounted = true;

    const checkAuth = async () => {
      try {
        const result = await fetchUserData();
        if (isMounted) {
          setAuthStatus(result?.success ? 'authenticated' : 'unauthenticated');
        }
      } catch (error) {
        if (isMounted) {
          setAuthStatus('unauthenticated');
        }
      }
    };

    checkAuth();

    return () => {
      isMounted = false;
    };
  }, [fetchUserData]);

  // Show loading state while checking
  if (authStatus === 'checking') {
    return (
      <Container>
        <Box sx={boxStyles}>
          <CircularProgress />
        </Box>
      </Container>
    );
  }

  return (
    <Container>
      <Box sx={boxStyles}>
        {authStatus === 'unauthenticated' ? (
          <Typography variant="h3" fontWeight="bold" sx={{ px: 30 }}>
            Welcome to iNoteBook, Please Login or Signup to Continue
          </Typography>
        ) : (
          <Typography variant="h3" fontWeight="bold" sx={{ px: 30 }}>
            Welcome {user?.name}, Feel Free to Make Notes!
          </Typography>
        )}
      </Box>
    </Container>
  );
};

const boxStyles = {
  height: `calc(100vh - ${NAVBAR_HEIGHT}px)`,
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
  textAlign: "center",
  padding: 2,
  boxSizing: "border-box"
};

export default Welcome;
