import { Typography, Box, Container, Button } from "@mui/material";
import React, { useContext, useEffect } from "react";
import UserContext from "../context/UserContext";
import { Link } from "react-router-dom";

const NAVBAR_HEIGHT = 64;

const Welcome = () => {
    let context = useContext(UserContext);
    const { fetchUserData, user } = context;

    useEffect(() => {
        const checkAuth = async () => {
            await fetchUserData();
        };
        checkAuth();
    }, []);

    return (
        <Container>
            <Box
                sx={{
                    height: `calc(100vh - ${NAVBAR_HEIGHT}px)`,
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    textAlign: "center",
                    padding: 2,
                    boxSizing: "border-box"
                }}
            >
                {user === undefined ? (
                    <Typography>Loading...</Typography>
                ) : (!user || !user.name) ? (
                    // Not logged in
                    <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
                        <Typography variant="h3" fontWeight="bold">
                            iNoteBook - Where Every Thought Finds Its Home. Start Your Journey!
                        </Typography>
                        <Typography>Please Login or Signup To Continue</Typography>
                        <Button component={Link} to="/login">Login</Button>
                        <Button component={Link} to="/signup">Signup</Button>
                    </Box>
                ) : (
                    // Logged in
                    <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
                        <Typography variant="h3" fontWeight="bold">
                            Great to see you, {user.name}! Your digital notebook awaits!
                        </Typography>
                        <Button variant='container' component={Link} to="/dashboard">Dashboard</Button>
                    </Box>
                )}
            </Box>
        </Container>
    );
};

export default Welcome;
