import React, { useState } from "react"; 
import NoteContext from "./NoteContext";

export default function NoteState(props) {
   const [notes, setNotes] = useState([]);
  const hardcodeNotes = [ ];
const getNotes = async () => {
  const response = await fetch('http://localhost:5000/api/notes/fetchallnotes', {
    method: 'GET',
    headers: {
      'Accept': '*/*',
      'User-Agent': 'Thunder Client (https://www.thunderclient.com)',
      'auth-token': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjg5MWZkNGQyNGQ1ODJhZjE1YjliN2JlIn0sImlhdCI6MTc1NDQ2MzgwMn0.gKhLAuMPCQNf0DjGdAzTVgHa85VmItPk408CXuOdMe8',
      'Content-Type': 'application/json'
    }
  });

  const allNotes = await response.json(); 
  console.log(allNotes);
  setNotes(allNotes)
};

  function addNote(title1, description1, tag1) {
    const newNote = {
      _id: Math.random().toString(), 
      user: "6891fd4d24d582af15b9b7be",
      title: title1,
      description: description1,
      tag: tag1,
      date: new Date().toISOString(),
      __v: 0
    };
    setNotes(notes.concat(newNote));
  }
function deleteNote(id){
  console.log("the id is"+id)
  setNotes(notes.filter((note)=>{return note._id!==id}))
}
function editNote(id,title,description,tag){
  for (let index = 0; index < notes.length; index++) {
    if(notes[index]._id===id){
      notes[index].title=title
      notes[index].description=description
      notes[index].desc=title
    }
  }
}
  return (
    <NoteContext.Provider value={{notes,addNote,deleteNote,getNotes}}>
      {props.children}
    </NoteContext.Provider>
  );
}
