import { Typography, Box, Container } from "@mui/material";
import React, { useContext, useEffect, useState } from "react";
import UserContext from "../context/UserContext";
const NAVBAR_HEIGHT = 64; 

const Welcome = () => {
    let context=useContext(UserContext);
    const {fetchUserData,user} = context
    const [authentication,setAuthentication]=useState(false)
useEffect(() => {
  let isMounted = true; 

  const checkAuth = async () => {
    try {
      const result = await fetchUserData();
      if (isMounted) { 
        setAuthentication(result && result.success);
      }
    } catch (error) {
      if (isMounted) {
        setAuthentication(false);
      }
    } finally {
      if (isMounted) {
        setAuthentication(false);
      }
    }
  };

  checkAuth();

  return () => {
    isMounted = false; // 2. Set to false when unmounting
  };
}, []);
  return (
    <Container>
        <Box
        sx={{
            height: `calc(100vh - ${NAVBAR_HEIGHT}px)`, 
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            textAlign: "center",
            padding: 2,
            boxSizing:"border-box"
        }}
        >
           {(!authentication)?
        <Typography variant="h3" fontWeight="bold" sx={{px:30}}>
            Welcome to iNoteBook, Please Login or Signup to Continue
        </Typography>: 
        <Typography variant="h3" fontWeight="bold" sx={{px:30}}>
            Welcome {user.name},Feel Free to Make Notes!
        </Typography>
        }
        </Box>
    </Container>
  );
};

export default Welcome;
