import { Typography, Box, Container, CircularProgress } from "@mui/material";
import React, { useContext, useEffect, useState } from "react";
import UserContext from "../context/UserContext";

const NAVBAR_HEIGHT = 64;

const Welcome = () => {
  const { fetchUserData, user } = useContext(UserContext);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const checkAuth = async () => {
      try {
        await fetchUserData(); // Wait for user data
      } finally {
        setLoading(false); // Stop loading whether success or fail
      }
    };
    checkAuth();
  }, [fetchUserData]);

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Container>
      <Box sx={boxStyles}>
        {user ? (
          <Typography variant="h3" fontWeight="bold" sx={{ px: 30 }}>
            Welcome {user.name}, Feel Free to Make Notes!
          </Typography>
        ) : (
          <Typography variant="h3" fontWeight="bold" sx={{ px: 30 }}>
            Welcome to iNoteBook, Please Login or Signup to Continue
          </Typography>
        )}
      </Box>
    </Container>
  );
};

const boxStyles = {
  height: `calc(100vh - ${NAVBAR_HEIGHT}px)`,
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
  textAlign: "center",
  padding: 2,
  boxSizing: "border-box"
};

export default Welcome;
