import React, { useState } from 'react'
import { Paper, Box, Typography, TextField, Button, Container } from "@mui/material";
import { useNavigate } from "react-router-dom";

const LoginPage = () => {
    const[credentials,setCredentials]=useState({email:"",password:""})
      const handleChange = (e) => {
        setCredentials({ ...credentials, [e.target.name]: e.target.value });
    };
    const navigate=useNavigate();
  const loginCheck=  async () => {
        const response = await fetch(`http://localhost:5000/api/auth/login`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({email: credentials.email, password: credentials.password})
        });
        let json = await response.json();
        if(json.success){
            localStorage.setItem("authToken",json.authToken)
            navigate('/')
        }else{
            alert("Invalid Credentials")
        }
    }
    const handleSubmit = (e) => {
        e.preventDefault()
        loginCheck();
    }
  return (
    <Container>
    <Paper
      elevation={3}
      sx={{
        maxWidth: 400,
        margin: "auto",
        mt: 8,
        p: 3,
        borderRadius: 2,
      }}
    >
        <Typography variant="h5" textAlign="center">
          Login
        </Typography>
      <TextField label="Email" name='email' value={credentials.email} onChange={handleChange} variant="outlined" fullWidth sx={{my:2}}/>
      <TextField label="Password" name='password' value={credentials.password} type="password" sx={{my:2}} onChange={handleChange} variant="outlined" fullWidth />
        <Button variant="contained"  onClick={handleSubmit} fullWidth>
          Login
        </Button>
      </Paper>

    </Container>
  );
};


export default LoginPage;
